javascript:
document.addEventListener('DOMContentLoaded', function() {
  const homeLink = document.querySelector('.navbar-brand');
  if (homeLink) {
    homeLink.href = "/app/scouting-%26-crop-protection";
  }
});
//hide other links
const navItems = document.getElementsByClassName('nav-item');
const itemsToHide = ["Scouts Map", "Observations Map", "Variety Map"];

for (let i = 0; i < navItems.length; i++) {
  const currentItemText = navItems[i].textContent.trim();

  if (itemsToHide.includes(currentItemText)) {
    navItems[i].style.display = 'none';
  }
}
