report_script:
if not filters:
    filters = {}

columns = [
    {"label": "Variety", "fieldname": "variety", "fieldtype": "Link", "options": "Item", "width": 200},
    {"label": "Pest", "fieldname": "pest", "fieldtype": "Data", "width": 150},
    {"label": "Scope", "fieldname": "scope", "fieldtype": "Data", "width": 120},
    {"label": "Low (% Area)", "fieldname": "low", "fieldtype": "Int", "width": 200},
    {"label": "Moderate (% Area)", "fieldname": "moderate", "fieldtype": "Int", "width": 200},
    {"label": "High (% Area)", "fieldname": "high", "fieldtype": "Int", "width": 200},
]

item_filters = {
    "disabled": 0,
    "item_group": ["in", ["Standard Roses", "Spray Roses", "Summer Flowers"]],
}
if filters.get("variety"):
    item_filters["name"] = filters["variety"]

items = frappe.get_all("Item", filters=item_filters, fields=["name"], order_by="name")

rows = []
for item in items:
    thresholds = frappe.get_all(
        "Chemical Requirements",
        filters={"parent": item.name},
        fields=["pest", "scope", "low", "moderate", "high"],
        order_by="pest, scope"
    )
    for t in thresholds:
        rows.append({
            "variety": item.name,
            "pest": t.pest,
            "scope": t.scope,
            "low": t.low,
            "moderate": t.moderate,
            "high": t.high,
        })

data = [columns, rows]
